import React, { useState, useEffect } from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'
import Card from '../components/Card'
import Carousal from '../components/Carousal'


export default function Home() {
    const [foodItems, setFooditems] = useState([]);
    const [foodCategory, setFoodCategory] = useState([]);

    async function fetchFoodData() {
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URI}/foods/fooddata`, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json; charset=UTF-8"
                },
            });

            if (!response.ok) {
                throw new Error("Failed to fetch food data");
            }

            const data = await response.json();
            console.log("Server Response:", data);

            setFooditems(data.foodItems);
            setFoodCategory(data.foodCategory);

        } catch (error) {
            console.error("Error fetching food data:", error.message);
        }
    }
    useEffect(() => {
        fetchFoodData();
    }, []);


    return (
        <>
            <div>
                <Navbar />
            </div>

            <div>
                <Carousal />
            </div>
            <div className='m-3'>
                {
                    foodCategory.length > 0 ? foodCategory.map((data) => {
                        return (
                            <>
                                <div key={data._id} className='fs-3 m-3'> {data.CategoryName} </div>
                                <hr />
                                {
                                    foodItems.length > 0 ? 
                                    foodItems.filter((item) => item.CategoryName === data.CategoryName)
                                    .map((filteredItems) =>{
                                        return (
                                            <div>fil.name</div>
                                        )
                                    })
                                    : <div>No Food In this Category</div>
                                }
                            </>

                        )
                    }) : <div> No Categories Found</div>
                }
                <Card />
            </div>
            <div>
                <Footer />
            </div>

        </>

    )
}
